implementation.source:

standardShaderLightingData standardShaderLightingCombiner(
                float3 N,
                float3 V,
                float3 L,
                float3 SL,
                float3 diffuse,
                float3 specular)
{
    standardShaderLightingData result;
    result.N = N;
    result.V = V;
    result.L = L;
    result.SL = SL;
    result.diffuse = diffuse;
    result.specular = specular;
    return result;
}


